import { useEffect, useState, useContext } from 'react';
import ProductCard from '../components/ProductCard';
import { Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

import UserContext from '../context/UserContext';

export default function Products() {
    const { user } = useContext(UserContext); 
    const [ products, setProducts ] = useState([]);

    const fetchData = () => {
        let fetchUrl = user.isAdmin === true ? `${process.env.REACT_APP_API_URL}/products/all` : `${process.env.REACT_APP_API_URL}/products/active`

       fetch(fetchUrl, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => res.json())
        .then(data => {

            setProducts(data)
        });
    }

    useEffect(() => {
        fetchData();
    }, [user]);

    return (
        <>
            {
                user ? (
                    products.length > 0 ? (
                        <>  
                            <h1 className='text-center mt-5'>Products Catalog</h1>
                            <Row> 
                                {   
                                    products.map(product => (
                                        <Col md={3} key={product._id}>
                                            <ProductCard productProp={product} />
                                        </Col>
                                    ))
                                }   
                            </Row>
                        </>
                    ) : (
                        <h1>No Products Available</h1>
                    )
                ) : (
                    <>
                        <h1>You are not logged in</h1>
                        <Link className="btn btn-primary" to={"/login"}>Login to View</Link>
                    </>
                )
            }
        </>
    );
}
