import { Card, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import PropTypes from 'prop-types';
import { useContext } from 'react';
import UserContext from '../context/UserContext';

export default function ProductCard({ productProp }) {
    const navigate = useNavigate();
    const { user } = useContext(UserContext); 

    if (!productProp) {
        return <div>No Products Available</div>;
    }


    const { _id, name, description, price, isActive } = productProp || {};

    const toggleProductStatus = (id) => {
        fetch(`${process.env.REACT_APP_API_URL}/products/${id}`, {
            method: 'PATCH',
            headers: {
                "Content-Type": "application/json",
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify({ isActive: !isActive }) // Toggle the status
        })
        .then(res => res.json())
        .then(data => {
            if (data.error) {
                Swal.fire({
                    icon: "error",
                    title: "Unsuccessful Product Update",
                    text: data.message
                });
            } else {
                Swal.fire({
                    icon: "success",
                    title: "Product Updated"
                });
                navigate('/products');
            }
        })
        .catch(error => {
            Swal.fire({
                icon: "error",
                title: "An error occurred",
                text: error.message
            });
        });
    }

    const deleteProduct = (id) => {
        fetch(`${process.env.REACT_APP_API_URL}/products/${id}`, {
            method: 'DELETE',
            headers: {
                "Content-Type": "application/json",
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            }
        })
        .then(res => res.json())
        .then(data => {
            if (data.error) {
                Swal.fire({
                    icon: "error",
                    title: "Unsuccessful Product Deletion",
                    text: data.message
                });
            } else {
                Swal.fire({
                    icon: "success",
                    title: "Product Deleted"
                });
                navigate('/products');
            }
        })
        .catch(error => {
            Swal.fire({
                icon: "error",
                title: "An error occurred",
                text: error.message
            });
        });
    }



    return (
        <Card className="mt-3">
            <Card.Body>
                <Card.Title>{name}</Card.Title>
                <Card.Subtitle>Description:</Card.Subtitle>
                <Card.Text>{description}</Card.Text>
                <Card.Subtitle>Price:</Card.Subtitle>
                <Card.Text>{price}</Card.Text>
                <Card.Subtitle>Status:</Card.Subtitle>
                <Card.Text>{isActive ? 'Active' : 'Inactive'}</Card.Text> {/* Display active/inactive status */}
            </Card.Body>
            {(user && user.isAdmin)?
                <Card.Footer className="d-flex justify-content-around">
                    <Button className="btn btn-primary btn-sm" onClick={() => toggleProductStatus(_id)}>
                        {isActive ? 'Deactivate' : 'Activate'} {/* Toggle button text */}
                    </Button>
                    <Button className="btn btn-danger btn-sm" onClick={() => deleteProduct(_id)}>Delete</Button>
                </Card.Footer>
            : ''}
        </Card>
    );
}

// Prop validation
ProductCard.propTypes = {
    product: PropTypes.shape({
        _id: PropTypes.string,
        name: PropTypes.string,
        description: PropTypes.string,
        isActive: PropTypes.bool // Updated to reflect the new field
    })
};
