import React, { useEffect, useState, useContext } from 'react';
import { Container } from 'react-bootstrap';
import UserContext from '../context/UserContext';
import AdminOrderView from '../components/AdminOrderView';
import UserOrderView from '../components/UserOrderView';

const OrderView = () => {
    const { user } = useContext(UserContext);
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const isAdmin = user.isAdmin;

    useEffect(() => {
        const fetchOrders = async () => {
            const url = isAdmin ? `${process.env.REACT_APP_API_URL}/orders/all-orders` : `${process.env.REACT_APP_API_URL}/orders/my-orders`;
            const token = localStorage.getItem('token');

            try {
                const response = await fetch(url, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                // if (!response.ok) {
                //     const errorBody = await response.text();
                //     throw new Error(`Failed to fetch orders. Status: ${response.status}, Body: ${errorBody}`);
                // }

                const data = await response.json();
                if (data.orders && data.orders.length > 0) {
                    setOrders(data.orders);
                } else {
                    setOrders([]);
                }
            } catch (error) {
                console.error('Error fetching orders:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, [isAdmin]);

    if (loading) return <div>Loading...</div>;

    if (error) return <div>Error: {error}</div>;

    return (
        <Container>
            <h2>{isAdmin ? 'All Orders' : 'My Orders'}</h2>
            {orders.length === 0 ? (
                <div>You have no pending orders</div>
            ) : (
                <UserOrderView orders={orders} />
            )}
        </Container>
    );
};

export default OrderView;
