import React from 'react';
import { Table } from 'react-bootstrap';

const AdminOrderView = ({ orders }) => {
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Ordered Item</th>
                    <th>Date</th>
                    <th>Customer Name</th>
                    <th>Price</th>
                </tr>
            </thead>
            <tbody>
                {orders.map(order => (
                    <tr key={order._id}>
                        <td>{order.productsOrdered.map(item => item.productId.productName).join(', ')}</td>
                        <td>{new Date(order.orderedOn).toLocaleDateString()}</td>
                        <td>{order.userId.name || 'N/A'}</td>
                        <td>â‚±{order.totalPrice.toLocaleString(undefined, { minimumFractionDigits: 2 })}</td>
                    </tr>
                ))}
            </tbody>
        </Table>
    );
};

export default AdminOrderView;
