import React, { useEffect, useContext, useState } from 'react';
import { Container, Button, Row, Col } from 'react-bootstrap';
import CartContext from '../context/CartContext';
import Swal from 'sweetalert2';
import CartItem from '../components/CartItem';

const CartView = () => {
    const { cart, setCart } = useContext(CartContext);
    const [loading, setLoading] = useState(true);
    const [inputValues, setInputValues] = useState({});
    const [selectedItems, setSelectedItems] = useState([]);

    useEffect(() => {
        const fetchCart = () => {
            const url = `${process.env.REACT_APP_API_BASE_URL}/cart/get-cart`;
            const token = localStorage.getItem('token');

            fetch(url, {
                headers: {
                    method: 'GET',
                    Authorization: `Bearer ${token}`
                }
            })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`Failed to fetch cart. Status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                if (data.cart) {
                    setCart(data.cart);
                    const initialInputValues = data.cart.cartItems.reduce((acc, item) => {
                        acc[item.productId] = item.quantity.toString();
                        return acc;
                    }, {});
                    setInputValues(initialInputValues);
                } else {
                    setCart({ cartItems: [], totalPrice: 0 });
                }
            })
            .catch(error => {
                console.error("Error fetching cart:", error);
                Swal.fire({
                    icon: 'error',
                    title: 'Error fetching cart',
                    text: error.message
                });
            })
            .finally(() => {
                setLoading(false);
            });
        };

        fetchCart();
    }, [setCart]);

    const handleInputChange = (productId, value) => {
        setInputValues(prev => ({
            ...prev,
            [productId]: value,
        }));
    };

    const handleQuantityBlur = (productId) => {
        const newQuantity = inputValues[productId];
        if (newQuantity === '') return;

        const parsedQuantity = parseInt(newQuantity, 10);
        if (!isNaN(parsedQuantity) && parsedQuantity >= 0) {
            handleUpdateQuantity(productId, parsedQuantity);
        } else {
            const originalQuantity = cart.cartItems.find(item => item.productId === productId).quantity;
            setInputValues(prev => ({
                ...prev,
                [productId]: originalQuantity.toString(),
            }));
        }
    };

    const handleRemoveItem = (productId, silent = false) => {
        const removeItem = () => {
            fetch(`${process.env.REACT_APP_API_BASE_URL}/cart/${productId}/remove-from-cart`, {
                method: 'PATCH',
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    // Update the cart by removing the specific product
                    const updatedCartItems = cart.cartItems.filter(item => item.productId !== productId);
                    setCart({
                        cartItems: updatedCartItems,
                        totalPrice: updatedCartItems.reduce((acc, item) => acc + item.subtotal, 0),
                    });

                    if (!silent) {
                        Swal.fire({
                            icon: 'success',
                            title: 'Item removed from cart successfully',
                        });
                    }

                    // If the cart is empty, display the empty message
                    if (updatedCartItems.length === 0) {
                        Swal.fire({
                            icon: 'info',
                            title: 'Your cart is now empty',
                        });
                    }
                } else {
                    throw new Error(data.message || 'Failed to remove item');
                }
            })
            .catch(error => {
                console.error("Error removing item:", error);
                Swal.fire({
                    icon: 'error',
                    title: 'Error removing item',
                    text: error.message
                });
            });
        };

        if (!silent) {
            Swal.fire({
                title: 'Are you sure?',
                text: "This will remove the item from your cart!",
                icon: 'warning',
                showCancelButton: true,
                confirmButtonText: 'Yes, remove it!',
                cancelButtonText: 'No, keep it'
            }).then((result) => {
                if (result.isConfirmed) {
                    removeItem();
                }
            });
        } else {
            removeItem();
        }
    };

    const handleUpdateQuantity = (productId, newQuantity) => {
        if (newQuantity <= 0) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Quantity',
                text: 'Quantity must be greater than zero',
            });
            return;
        }

        fetch(`${process.env.REACT_APP_API_BASE_URL}/cart/${productId}/update-cart-quantity`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({ quantity: newQuantity })
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                setCart(data.updatedCart);
                console.log('Quantity updated successfully');
            } else {
                throw new Error(data.message || 'Failed to update quantity');
            }
        })
        .catch(error => {
            console.error("Error updating quantity:", error);
            Swal.fire({
                icon: 'error',
                title: 'Error updating quantity',
                text: error.message
            });
        });
    };

    const handleClearCart = () => {
        Swal.fire({
            title: 'Are you sure?',
            text: "This will remove all items from your cart!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, clear it!',
            cancelButtonText: 'No, keep it'
        }).then((result) => {
            if (result.isConfirmed) {
                fetch(`${process.env.REACT_APP_API_BASE_URL}/cart/clear-cart`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${localStorage.getItem('token')}`
                    }
                })
                .then(response => response.json())
                .then(data => {
                    if (data.success) {
                        setCart({ cartItems: [], totalPrice: 0 });
                        Swal.fire({
                            icon: 'success',
                            title: 'Cart cleared successfully',
                        });
                    } else {
                        throw new Error(data.message || 'Failed to clear cart');
                    }
                })
                .catch(error => {
                    console.error("Error clearing cart:", error);
                    Swal.fire({
                        icon: 'error',
                        title: 'Error clearing cart',
                        text: error.message
                    });
                });
            }
        });
    };

    const handleSelectItem = (productId) => {
        setSelectedItems(prev => {
            if (prev.includes(productId)) {
                return prev.filter(id => id !== productId);
            } else {
                return [...prev, productId];
            }
        });
    };

    const handleCheckout = () => {
        if (selectedItems.length === 0) {
            Swal.fire({
                icon: 'warning',
                title: 'No items selected',
                text: 'Please select items to checkout',
            });
            return;
        }

        const selectedCartItems = cart.cartItems.filter(item => selectedItems.includes(item.productId));
        const totalAmount = selectedCartItems.reduce((acc, item) => acc + item.subtotal, 0);

        if (totalAmount === 0) {
            Swal.fire({
                icon: 'warning',
                title: 'No items to checkout',
                text: 'The selected items have a total of ₱0. Please adjust your selections.',
            });
            return;
        }

        Swal.fire({
            title: 'Proceed to checkout?',
            text: `Total amount ₱${totalAmount.toLocaleString()}`,
            icon: 'question',
            showCancelButton: true,
            confirmButtonText: 'Yes, proceed',
            cancelButtonText: 'Cancel',
        }).then((result) => {
            if (result.isConfirmed) {
                const orderPayload = {
                    productsOrdered: selectedCartItems.map(item => ({
                        productId: item.productId,
                        quantity: item.quantity,
                        subtotal: item.subtotal,
                    })),
                    // Note: totalPrice is no longer required in the payload since the backend calculates it
                };

                fetch(`${process.env.REACT_APP_API_BASE_URL}/orders/checkout`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                    body: JSON.stringify(orderPayload),
                })
                .then(res => res.json())
                .then(data => {
                    if (data.order) {
                        Swal.fire({
                            icon: 'success',
                            title: 'Checkout Successful',
                            text: `You have successfully ordered ${selectedCartItems.length} item(s) with a total amount of ₱${data.order.totalPrice.toLocaleString()}`,
                        });

                        // Remove only selected items from the cart
                        const updatedCartItems = cart.cartItems.filter(item => !selectedCartItems.some(selectedItem => selectedItem.productId === item.productId));

                        // Update the cart state
                        setCart({
                            cartItems: updatedCartItems,
                            totalPrice: updatedCartItems.reduce((acc, item) => acc + item.subtotal, 0)
                        });

                        // Clear selected items
                        setSelectedItems([]); // Clear selected items
                    } else {
                        Swal.fire({
                            icon: 'error',
                            title: 'Checkout Failed',
                            text: data.message || 'Something went wrong during checkout. Please try again.',
                        });
                    }
                })
                .catch(error => {
                    console.error('Checkout Error:', error);
                    Swal.fire({
                        icon: 'error',
                        title: 'Checkout Failed',
                        text: 'An error occurred during checkout. Please try again.',
                    });
                });
            }
        });
    };

    if (loading) { return <div>Loading cart</div>; }

    return (
        <Container className="mt-4">
            <Row className="mt-3">
                <div className="col-md-12 d-flex justify-content-between">
                    <h2>Your Cart</h2>
                    <div style={{ marginBottom: '20px' }}>
                    {cart && cart.cartItems && cart.cartItems.length > 0 ?
                        <Button variant="danger" onClick={handleClearCart}>
                            Clear Cart
                        </Button>
                    : null }
                    {cart && cart.cartItems && cart.cartItems.length > 0 ?
                        <Button variant="success" onClick={handleCheckout} >
                            Checkout Selected Items
                        </Button>
                    : null }
                    </div>
                </div>
            </Row>
            {cart.cartItems.length > 0 ? (
                <>
                    <Row>
                        {cart.cartItems.map((item) => (
                            <Col key={item.productId} md={6} lg={4}>
                                <CartItem
                                    item={item}
                                    inputValue={inputValues[item.productId]}
                                    onInputChange={handleInputChange}
                                    onQuantityBlur={handleQuantityBlur}
                                    onRemoveItem={handleRemoveItem}
                                    onUpdateQuantity={handleUpdateQuantity}
                                    onSelectItem={handleSelectItem}
                                    isSelected={selectedItems.includes(item.productId)}
                                />
                            </Col>
                        ))}
                    </Row>
                </>
            ) : (
                <Row>
                    <Col md={12}>
                        <h4>Your cart is empty</h4>
                    </Col>
                </Row>
            )}
        </Container>
    );
};

export default CartView;
