import React from 'react';
import { Table } from 'react-bootstrap';

const UserOrderView = ({ orders }) => {
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Ordered Item</th>
                    <th>Date</th>
                    <th>Price</th>
                </tr>
            </thead>
            <tbody>
                {orders.map(order => (
                    <tr key={order._id}>
                        <td>
                            {order.productsOrdered.map((item, index) => (
                                <div key={item.productId._id}>
                                    {index + 1}. {item.productId.name} <br />
                                    Quantity: {item.quantity} <br/> Price: ₱{item.productId.price}<br/>
                                </div>
                            ))}
                        </td>
                        <td>
                            {new Date(order.orderedOn).toLocaleDateString()}
                        </td>
                        <td>
                            ₱{order.totalPrice.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                        </td>
                    </tr>
                ))}
            </tbody>
        </Table>
    );
};

export default UserOrderView;
