import React, { useEffect, useContext, useState } from 'react';
import { Container, ListGroup, Button } from 'react-bootstrap';
import CartContext from '../context/CartContext'; // Adjust this import according to your context setup
import Swal from 'sweetalert2';

const CartView = () => {
    const { cart, setCart } = useContext(CartContext); // Ensure CartContext is set up correctly
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCart = () => {
            const url = `${process.env.REACT_APP_API_URL}/cart/get-cart`;
            const token = localStorage.getItem('token');
            //console.log('Fetching cart from:', url);
            //console.log('Authorization header:', `Bearer ${token}`);

            fetch(url, {
                headers: {
                    method: 'GET',
                    Authorization: `Bearer ${token}`
                }
            })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`Failed to fetch cart. Status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                //console.log('Cart data received:', data);
                if (data.cart) {
                    setCart(data.cart);
                } else {
                    setCart({ cartItems: [], totalPrice: 0 });
                }
            })
            .catch(error => {
                console.error("Error fetching cart:", error);
                error.json && error.json().then(errorDetails => {
                    console.error("Error details:", errorDetails);
                    Swal.fire({
                        icon: 'error',
                        title: 'Error fetching cart',
                        text: errorDetails.message || error.message
                    });
                }).catch(() => {
                    Swal.fire({
                        icon: 'error',
                        title: 'Error fetching cart',
                        text: error.message
                    });
                });
            })
            .finally(() => {
                setLoading(false);
            });
        };

        fetchCart();
    }, [setCart]);

    const handleRemoveItem = (productId) => {
        fetch(`${process.env.REACT_APP_API_URL}/cart/${productId}/remove-from-cart`, {
            method: 'PATCH',
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                setCart(data.updatedCart); // Update the cart in context or state
                Swal.fire({
                    icon: 'success',
                    title: 'Item removed from cart successfully',
                });
            } else {
                throw new Error(data.message || 'Failed to remove item');
            }
        })
        .catch(error => {
            console.error("Error removing item:", error);
            Swal.fire({
                icon: 'error',
                title: 'Error removing item',
                text: error.message
            });
        });
    };

    if (loading) return <div>Loading...</div>;

    return (
        <Container>
            <h2>Your Cart</h2>
            {cart && cart.cartItems && cart.cartItems.length > 0 ? (
                <ListGroup>
                    {cart.cartItems.map(item => (
                        <ListGroup.Item key={item.productId}>
                            <div>
                                <strong>Product:</strong> {item.productId.name} <br />
                                <strong>Quantity:</strong>
                                <input 
                                    type="number" 
                                    value={item.quantity} 
                                    min="1"
                                    onChange={(e) => handleUpdateQuantity(item.productId, parseInt(e.target.value, 10))}
                                /> <br />
                                <strong>Subtotal:</strong> ₱{item.subtotal.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                            </div>
                            <Button variant="danger" onClick={() => handleRemoveItem(item.productId)}>
                                Remove
                            </Button>
                        </ListGroup.Item>
                    ))}
                </ListGroup>
            ) : (
                <div>Your cart is empty.</div>
            )}
            <h4>Total Price: ₱{cart && cart.totalPrice ? cart.totalPrice.toLocaleString(undefined, { minimumFractionDigits: 2 }) : '0.00'}</h4>
        </Container>
    );
};

export default CartView;
