import { CardGroup } from 'react-bootstrap';
import { Row, Col } from 'react-bootstrap';
import { useState, useEffect } from 'react';
import ProductCard from './ProductCard';

export default function FeaturedProducts() {
    const [previews, setPreviews] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_BASE_URL}/products/active`)
            .then(res => res.json())
            .then(data => {
                console.log(data);

                const numbers = [];
                const featured = [];

                const generateRandomNums = () => {
                    let randomNum = Math.floor(Math.random() * data.length);

                    if (numbers.indexOf(randomNum) === -1) {
                        numbers.push(randomNum);
                    } else {
                        generateRandomNums();
                    }
                };

                // Limit to 3 random products
                for (let i = 0; i < 3; i++) {
                    generateRandomNums();
                    featured.push(
                        <ProductCard
                            data={data[numbers[i]]}
                            key={data[numbers[i]]._id}
                            breakPoint={4} 
                            className="m-3" // Adding margin here
                        />
                    );
                }

                setPreviews(featured);
            });
    }, []);

    return (
        <>
            <h2 className="text-center">Featured Products</h2>

            <Row>
                {previews.length > 0 ? (
                    previews.map(product => (
                        <Col md={3} key={previews._id} className="mb-4">
                            <ProductCard 
                                productProp={previews} 
                            />
                        </Col>
                    ))
                ) : (
                    <Col md={12}>
                        <h4>{error || 'No products available'}</h4>
                    </Col>
                )}
            </Row>
        </>
    );
}
