import { useEffect, useState, useContext, useCallback } from 'react';
import ProductCard from '../components/ProductCard';
import { Row, Col, Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

import UserContext from '../context/UserContext';

export default function Products() {
    const { user } = useContext(UserContext); 
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [error, setError] = useState('');

    const fetchData = useCallback(() => {
        let fetchUrl = `${process.env.REACT_APP_API_URL}/products/archive`;

        fetch(fetchUrl, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => res.json())
        .then(data => {
            setProducts(data);
        });
    }, []);

    useEffect(() => {
        if (searchTerm.trim() === '') {
            fetchData(); // Fetch all active products if search term is empty
        }
    }, [searchTerm, fetchData]);

    const handleToggle = (id) => {
        setProducts((prevProducts) => prevProducts.filter(product => product._id !== id));
    }

    const handleSearch = (event) => {
        event.preventDefault();
        const fetchUrl = `${process.env.REACT_APP_API_URL}/products/search-by-name`;

        fetch(fetchUrl, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({ productName: searchTerm, IsActive: false }) // Use productName as per your backend
        })
        .then(res => {
            if (!res.ok) {
                if (res.status === 404) {
                    setProducts([]);
                    setError('No products found');
                } else {
                    console.log("Failed to search products'");
                }
            } else {
                return res.json();
            }
        })
        .then(data => {
            if (data && data.products) {
                setProducts(data.products);
            }
            setError('');
        })
        .catch(err => {
            setError(err.message);
            setProducts([]);
        });
    }

    return (
        <>
            {user ? (
                products.length > 0 ? (
                    <>  <Row className="mt-3">
                            <div className="col-md-12 d-flex justify-content-between align-items-center">
                                <h1>Products Catalog</h1>
                                <div className="d-flex search-con">
                                    {user.isAdmin === true ? (
                                        <div>
                                            <Link className="btn btn-primary" to={"/products"}>View Active</Link>
                                            <Link className="btn btn-primary" to={"/addProduct"}>Add Product</Link>
                                        </div>

                                    ) : null}
                                    <Form onChange={handleSearch}> {/* Changed onChange to onSubmit */}
                                        <Form.Group controlId="search">
                                            <Form.Control
                                                type="text"
                                                placeholder="Search Product List"
                                                value={searchTerm}
                                                onChange={(e) => setSearchTerm(e.target.value)}
                                            />
                                        </Form.Group>
                                    </Form>
                                </div>
                            </div>
                        </Row>
                        <Row> 
                            {products.map(product => (
                                <Col md={3} key={product._id}>
                                    <ProductCard 
                                        productProp={product} 
                                        onToggle={handleToggle}
                                    />
                                </Col>
                            ))}
                        </Row>
                    </>
                ) : (
                    <h1>No Products Available</h1>
                )
            ) : (
                <>
                    <h1>You are not logged in</h1>
                    <Link className="btn btn-primary" to={"/login"}>Login to View</Link>
                </>
            )}
        </>
    );
}
