import { useContext } from 'react';
import UserContext from '../context/UserContext';
import { Table } from 'react-bootstrap';

const UserOrderView = ({ orders }) => {
    const { user } = useContext(UserContext);
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    {user.isAdmin ? <th>Customer Name</th> : null }
                    <th>Ordered Item</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Order Price</th>
                </tr>
            </thead>
            <tbody>
                {orders.map(order => (
                    <tr key={order._id}>
                        {user.isAdmin ?
                            <td>
                                {`${order.userId.firstName} ${order.userId.lastName}` || 'N/A'}<br/>
                                <span className="email">Email: {order.userId.email}</span>
                            </td>
                        : null }
                        <td>
                            {order.productsOrdered.map((item, index) => (
                                <div key={item.productId._id}>
                                    {index + 1}. {item.productId.name} <br />
                                    Quantity: {item.quantity} <br/> Price: ₱{item.productId.price}
                                </div>
                            ))}
                        </td>
                        <td>
                            {new Date(order.orderedOn).toLocaleDateString()}
                        </td>
                        <td>
                            {order.status}
                        </td>
                        <td>
                            ₱{order.totalPrice.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                        </td>
                    </tr>
                ))}
            </tbody>
        </Table>
    );
};

export default UserOrderView;
