import { useEffect, useState, useContext, useCallback } from 'react';
import ProductCard from '../components/ProductCard';
import { Row, Col, Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import UserContext from '../context/UserContext';

export default function Products() {
    const { user } = useContext(UserContext);
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [error, setError] = useState('');
    const [isArchivedView, setIsArchivedView] = useState(false); // New state to toggle views

    const fetchData = useCallback(() => {
        const fetchUrl = isArchivedView 
            ? `${process.env.REACT_APP_API_URL}/products/archive`
            : `${process.env.REACT_APP_API_URL}/products/active`;

        fetch(fetchUrl, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => {
            if (!res.ok) throw new Error('Failed to fetch products');
            return res.json();
        })
        .then(data => {
            setProducts(data);
            setError(''); // Clear previous error
        })
        .catch(err => setError(err.message));
    }, [isArchivedView]);

    useEffect(() => {
        fetchData(); // Fetch products based on view type
    }, [fetchData]);

    const handleToggle = (id) => {
        setProducts(prevProducts => prevProducts.filter(product => product._id !== id));
    }

    const handleSearch = (event) => {
        event.preventDefault();
        const fetchUrl = `${process.env.REACT_APP_API_URL}/products/search-by-name`;

        fetch(fetchUrl, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({ productName: searchTerm, isActive: !isArchivedView }) // Use isActive based on view type
        })
        .then(res => {
            if (!res.ok) {
                if (res.status === 404) {
                    setProducts([]);
                    setError('No products found');
                } else {
                    throw new Error("Failed to search products");
                }
            }
            return res.json();
        })
        .then(data => {
            if (data && data.products) {
                setProducts(data.products);
                setError('');
            }
        })
        .catch(err => {
            setError(err.message);
            setProducts([]);
        });
    }

    return (
        <>
            {user ? (
                <>  
                    <Row className="mt-3">
                        <div className="col-md-12 d-flex justify-content-between align-items-center">
                            <h1>Products Catalog</h1>
                            <div className="d-flex search-con">
                                {user.isAdmin && (
                                    <div>
                                        <Button
                                            variant="primary"
                                            onClick={() => setIsArchivedView(false)}
                                            className={`ml-1 ${!isArchivedView ? 'active' : ''}`}
                                        >
                                            View Active
                                        </Button>
                                        <Button
                                            variant="primary"
                                            onClick={() => setIsArchivedView(true)}
                                            className={`ml-1 ${isArchivedView ? 'active' : ''}`}
                                        >
                                            View Archived
                                        </Button>
                                        <Link className="btn btn-primary ml-1" to={"/addProduct"}>Add Product</Link>
                                    </div>
                                )}
                                <Form onChange={handleSearch}>
                                    <Form.Group controlId="search">
                                        <Form.Control
                                            type="text"
                                            placeholder="Search Product List"
                                            value={searchTerm}
                                            onChange={(e) => setSearchTerm(e.target.value)}
                                        />
                                    </Form.Group>
                                </Form>
                            </div>
                        </div>
                    </Row>
                    <Row>
                        {products.length > 0 ? (
                            products.map(product => (
                                <Col md={3} key={product._id} className="mb-4">
                                    <ProductCard 
                                        productProp={product} 
                                        onToggle={handleToggle}
                                    />
                                </Col>
                            ))
                        ) : (
                            <Col md={12}>
                                <h4>{error || 'No products available'}</h4>
                            </Col>
                        )}
                    </Row>
                </>
            ) : (
                <>
                    <h1>You are not logged in</h1>
                    <Link className="btn btn-primary" to={"/login"}>Login to View</Link>
                </>
            )}
        </>
    );
}
