import { Card, Button, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2';
import PropTypes from 'prop-types';
import { useState, useContext } from 'react';
import UserContext from '../context/UserContext';
import CartContext from '../context/CartContext'; // Import CartContext

export default function ProductCard({ productProp, onToggle }) {
    const { user } = useContext(UserContext);
    const { setCart } = useContext(CartContext); // Use CartContext to update the cart

    const [quantity, setQuantity] = useState(1); // State to store the quantity

    if (!productProp) {
        return <div>No Products Available</div>;
    }

    const { _id, name, description, price, isActive } = productProp || {};

    const toggleStatus = (id, isActive) => {

        Swal.fire({
            title: `Are you sure you want to ${isActive ? 'archive' : 'activate'} the product?`,
            text: `This will remove the item on the ${isActive ? 'active' : 'archived'} product list!`,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, proceed',
            cancelButtonText: 'No, cancel'
        }).then((result) => {
            if (result.isConfirmed) {
                const url = isActive ? `${process.env.REACT_APP_API_BASE_URL}/products/${id}/archive` : `${process.env.REACT_APP_API_BASE_URL}/products/${id}/activate`;

                fetch(url, {
                    method: 'PATCH',
                    headers: {
                        "Content-Type": "application/json",
                        'Authorization': `Bearer ${localStorage.getItem("token")}`
                    },
                    body: JSON.stringify({ isActive: !isActive })
                })
                .then(res => res.json())
                .then(data => {
                    if (data.error) {
                        Swal.fire({
                            icon: "error",
                            title: "Unsuccessful Product Status Update",
                            text: data.message
                        });
                    } else {
                        Swal.fire({
                            icon: "success",
                            title: "Product Status Updated Successfully"
                        });
                        onToggle(id); // Notify parent component to remove the card
                    }
                })
                .catch(error => {
                    Swal.fire({
                        icon: "error",
                        title: "An error occurred",
                        text: error.message
                    });
                });
            }
        });
    };

    const addToCart = (name) => {
        const token = localStorage.getItem('token');
        if (!token) {
            Swal.fire({
                icon: "warning",
                title: "Please log in",
                text: "You need to first log in order to add items to your cart.",
                confirmButtonText: "Go to Login"
            }).then(() => {
                window.location.href = "/login";
            });
            return;
        }
        if (quantity < 1 || !quantity) {
            Swal.fire({
                icon: "error",
                title: "Invalid Quantity",
                text: "Please enter a quantity of at least 1."
            });
            return;
        }

        fetch(`${process.env.REACT_APP_API_BASE_URL}/cart/add-to-cart`, {
            method: 'POST',
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${token}`
            },
            body: JSON.stringify({
                productId: _id,
                quantity: quantity,
                subtotal: price * quantity
            })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                setCart(prevCart => {
                    // If prevCart is an object with a cartItems property, use that, otherwise ensure it's an array
                    const currentCartItems = Array.isArray(prevCart) ? prevCart : prevCart.cartItems || [];

                    // Update cart with the new item(s)
                    return [...currentCartItems, ...data.cart.cartItems];
                });

                setQuantity(1);

                Swal.fire({
                    icon: "success",
                    title: name,
                    //text: data.message
                    text: "is added to cart successfully!"
                });
            } else {
                Swal.fire({
                    icon: "error",
                    title: "Error adding item to cart",
                    text: data.message
                });
            }
        })
        .catch(error => {
            Swal.fire({
                icon: "error",
                title: "An error occurred",
                text: error.message
            });
        });
    };

    return (
        <Card>
            <Card.Body>
                <Card.Title>{name}</Card.Title>
                <Card.Subtitle>Description:</Card.Subtitle>
                <Card.Text>{description}</Card.Text>
                <Card.Subtitle>Price:</Card.Subtitle>
                <Card.Text>{price}</Card.Text>
            </Card.Body>
            <Card.Footer className="d-flex justify-content-between flex-column">
                {user.isAdmin === false ? (
                    <>
                        <Form.Group className="d-flex align-items-center">
                            <Form.Label htmlFor={`product-${_id}`}>Quantity:&nbsp;&nbsp;</Form.Label>
                            <Form.Control 
                                id={`product-${_id}`}
                                type="number" 
                                value={quantity} 
                                onChange={(e) => setQuantity(parseInt(e.target.value))} // Update quantity on change
                                min="1" // Minimum value set to 1
                            />
                        </Form.Group>
                        <Button variant="success" className="mt-2" onClick={() => addToCart(name)}>
                            Add to Cart
                        </Button>
                    </>
                ) : null}

                {(user && user.isAdmin) ? (
                    <div className="d-flex justify-content-around">
                        {isActive && (
                            <Link className="btn btn-primary btn-sm" to={`/UpdateProduct/${_id}`}>Update Product Info</Link>
                        )}
                        <Button className="btn btn-primary btn-sm" onClick={() => toggleStatus(_id, isActive)}>
                            {isActive ? 'Deactivate' : 'Activate'}
                        </Button>
                    </div>
                ) : null}
            </Card.Footer>
        </Card>
    );
}

// Prop validation
ProductCard.propTypes = {
    productProp: PropTypes.shape({
        _id: PropTypes.string,
        name: PropTypes.string,
        description: PropTypes.string,
        price: PropTypes.number,
        isActive: PropTypes.bool // Updated to reflect the new field
    }),
    onToggle: PropTypes.func
};
