import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2';
import PropTypes from 'prop-types';
import { useContext } from 'react';
import UserContext from '../context/UserContext';

export default function ProductCard({ productProp, onToggle }) {
    const { user } = useContext(UserContext); 

    if (!productProp) {
        return <div>No Products Available</div>;
    }

    const { _id, name, description, price, isActive } = productProp || {};

    const toggleStatus = (id, isActive) => {
        const url = isActive  ? `${process.env.REACT_APP_API_URL}/products/${id}/archive` : `${process.env.REACT_APP_API_URL}/products/${id}/activate`;

        fetch(url, {
            method: 'PATCH',
            headers: {
                "Content-Type": "application/json",
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify({ isActive: !isActive })
        })
        .then(res => res.json())
        .then(data => {
            if (data.error) {
                Swal.fire({
                    icon: "error",
                    title: "Unsuccessful Product Status Update",
                    text: data.message
                });
            } else {
                Swal.fire({
                    icon: "success",
                    title: "Product Status Updated Successfully"
                });
                onToggle(id); // Notify parent component to remove the card
            }
        })
        .catch(error => {
            Swal.fire({
                icon: "error",
                title: "An error occurred",
                text: error.message
            });
        });
    }

    {/*By default the active products are displayed and not literally all, archived products will be on separate page*/}

    return (
        <Card className="mt-3">
            <Card.Body>
                <Card.Title>{name}</Card.Title>
                <Card.Subtitle>Description:</Card.Subtitle>
                <Card.Text>{description}</Card.Text>
                <Card.Subtitle>Price:</Card.Subtitle>
                <Card.Text>{price}</Card.Text>
            </Card.Body>
            {(user && user.isAdmin )?
                <Card.Footer className="d-flex justify-content-around">
                    {(isActive) ?
                        <Link className="btn btn-primary btn-sm" to={"/UpdateProduct/{_id}"}>Update Product Info</Link>
                    : ''}
                    <Button className="btn btn-primary btn-sm" onClick={() => toggleStatus(_id, isActive)}>
                        { isActive === true ? 'Deactivate' : 'Activate' }
                    </Button>
                </Card.Footer>
            : ''}
        </Card>
    );
}

// Prop validation
ProductCard.propTypes = {
    productProp: PropTypes.shape({
        _id: PropTypes.string,
        name: PropTypes.string,
        description: PropTypes.string,
        isActive: PropTypes.bool // Updated to reflect the new field
    }),
    onToggle: PropTypes.func
};
