import { useEffect, useState, useContext, useCallback } from 'react';
import ProductCard from '../components/ProductCard';
import { Row, Col, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';

import UserContext from '../context/UserContext';

export default function Products() {
    const { user } = useContext(UserContext); 
    const [products, setProducts] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    const fetchData = useCallback(() => {
        let fetchUrl = `${process.env.REACT_APP_API_URL}/products/active`;

        fetch(fetchUrl, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => res.json())
        .then(data => {
            setProducts(data);
        });
    }, []);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleToggle = (id) => {
        setProducts((prevProducts) => prevProducts.filter(product => product._id !== id));
    }

    const handleSearch = (event) => {
        const value = event.target.value;
        setSearchTerm(value);

        const fetchUrl = `${process.env.REACT_APP_API_URL}/products/search-by-name`;

        fetch(fetchUrl, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({ name: value })
        })
        .then(res => res.json())
        .then(data => {
            setProducts(data);
        });
    };

    return (
        <>
            {user ? (
                <>
                    <Row className="mt-3">
                        <div className="col-md-12 d-flex justify-content-between align-items-center">
                            <h1>Products Catalog</h1>
                            <div>
                                <Link className="btn btn-primary" to={"/archivedProducts"}>View Archived</Link>
                                <Link className="btn btn-primary" to={"/addProduct"}>Add Product</Link>
                            </div>
                        </div>
                    </Row>
                    <Form className="mb-3">
                        <Form.Group controlId="search">
                            <Form.Control
                                type="text"
                                placeholder="Search by product name"
                                value={searchTerm}
                                onChange={handleSearch}
                            />
                        </Form.Group>
                    </Form>
                    <Row> 
                        {products.length > 0 ? (
                            products.map(product => (
                                <Col md={3} key={product._id}>
                                    <ProductCard 
                                        productProp={product} 
                                        onToggle={handleToggle}
                                    />
                                </Col>
                            ))
                        ) : (
                            <h1>No Products Available</h1>
                        )}
                    </Row>
                </>
            ) : (
                <>
                    <h1>You are not logged in</h1>
                    <Link className="btn btn-primary" to={"/login"}>Login to View</Link>
                </>
            )}
        </>
    );
}
