import React, { useEffect, useState, useCallback } from 'react';
import { Card, Button } from 'react-bootstrap';

const CartItem = ({ item, inputValue, onInputChange, onQuantityBlur, onRemoveItem, onUpdateQuantity, onSelectItem, isSelected }) => {
    const [product, setProduct] = useState(null);

    const fetchProduct = useCallback(() => {
        const fetchUrl = `${process.env.REACT_APP_API_URL}/products/${item.productId}`;

        fetch(fetchUrl, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => res.json())
        .then(data => {
            if (data.product) {
                setProduct(data.product);
            } else {
                console.error('Product data structure is not as expected:', data);
            }
        })
        .catch(error => {
            console.error('Error fetching product details:', error);
        });
    }, [item.productId]);

    useEffect(() => {
        fetchProduct();
    }, [fetchProduct]);

    if (!product) {
        return <div>Loading...</div>; // Display a loading message while the product data is being fetched
    }

    return (
        <Card className="mb-3">
            <Card.Body className="cart-body">
                <div className="d-flex align-items-start">
                    {/* Checkbox for selecting item */}
                    <input 
                        type="checkbox" 
                        checked={isSelected} 
                        onChange={() => onSelectItem(item.productId)} 
                        style={{ margin: '8px 10px 0 0' }} 
                    />
                    <div>
                        <Card.Title>{product.name}</Card.Title>
                        <Card.Text>{product.description}</Card.Text>
                    </div>
                </div>
                <div className="card-bottom">
                    <div className="">
                        <strong>Quantity:</strong>
                        <input
                            type="number"
                            value={inputValue || ''}
                            min="0"
                            onChange={(e) => onInputChange(item.productId, e.target.value)}
                            onBlur={() => {
                                onQuantityBlur(item.productId);
                                onUpdateQuantity(item.productId, inputValue);
                            }}
                            style={{ width: '60px', marginLeft: '10px' }}
                        />
                    </div>
                    <div>
                        <strong>Subtotal:</strong> â‚±{item.subtotal.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                    </div>
                </div>
            </Card.Body>
            <Card.Footer>
                <Button className="w-100" variant="danger" onClick={() => onRemoveItem(item.productId)}>
                    Remove
                </Button>
            </Card.Footer>
        </Card>
    );
};

export default CartItem;
