import { useEffect, useState, useContext, useCallback } from 'react';
import ProductCard from '../components/ProductCard';
import { Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

import UserContext from '../context/UserContext';

export default function Products() {
    const { user } = useContext(UserContext); 
    const [products, setProducts] = useState([]);

    const fetchData = useCallback(() => {
        //let fetchUrl = user.isAdmin === true ? `${process.env.REACT_APP_API_URL}/products/all` : `${process.env.REACT_APP_API_URL}/products/active`;
        //Admin can only view active, separate page for activating
        let fetchUrl = `${process.env.REACT_APP_API_URL}/products/active`;

        fetch(fetchUrl, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(res => res.json())
        .then(data => {
            setProducts(data);
        });
    }, [user]);

    useEffect(() => {
        fetchData();
    }, [fetchData]);
    console.log("User Object:", user);
    return (
        <>
            {user ? (
                products.length > 0 ? (
                    <>  <Row className="mt-3">
                            <div className="col-md-12 d-flex">
                                <h1 className='mt-5'>Products Catalog</h1>
                                <div className="d-flex justify-content-between">
                                    <Link className="btn btn-primary" to={"/archivedProducts"}>View Archived</Link>
                                <Link className="btn btn-primary" to={"/addProduct"}>Add Product</Link>
                                </div>
                            </div>
                        </Row>
                        <Row> 
                            {products.map(product => (
                                <Col md={3} key={product._id}>
                                    <ProductCard productProp={product} />
                                </Col>
                            ))}
                        </Row>
                    </>
                ) : (
                    <h1>No Products Available</h1>
                )
            ) : (
                <>
                    <h1>You are not logged in</h1>
                    <Link className="btn btn-primary" to={"/login"}>Login to View</Link>
                </>
            )}
        </>
    );
}
