import React, { useEffect, useContext, useState } from 'react';
import { Container, Button, Row, Col } from 'react-bootstrap';
import CartContext from '../context/CartContext';
import Swal from 'sweetalert2';
import CartItem from '../components/CartItem';

const CartView = () => {
    const { cart, setCart } = useContext(CartContext);
    const [loading, setLoading] = useState(true);
    const [inputValues, setInputValues] = useState({});
    const [selectedItems, setSelectedItems] = useState([]);

    useEffect(() => {
        const fetchCart = () => {
            const url = `${process.env.REACT_APP_API_URL}/cart/get-cart`;
            const token = localStorage.getItem('token');

            fetch(url, {
                headers: {
                    method: 'GET',
                    Authorization: `Bearer ${token}`
                }
            })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`Failed to fetch cart. Status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                if (data.cart) {
                    setCart(data.cart);
                    const initialInputValues = data.cart.cartItems.reduce((acc, item) => {
                        acc[item.productId] = item.quantity.toString();
                        return acc;
                    }, {});
                    setInputValues(initialInputValues);
                } else {
                    setCart({ cartItems: [], totalPrice: 0 });
                }
            })
            .catch(error => {
                console.error("Error fetching cart:", error);
                Swal.fire({
                    icon: 'error',
                    title: 'Error fetching cart',
                    text: error.message
                });
            })
            .finally(() => {
                setLoading(false);
            });
        };

        fetchCart();
    }, [setCart]);

    const handleInputChange = (productId, value) => {
        setInputValues(prev => ({
            ...prev,
            [productId]: value,
        }));
    };

    const handleQuantityBlur = (productId) => {
        const newQuantity = inputValues[productId];
        if (newQuantity === '') return;

        const parsedQuantity = parseInt(newQuantity, 10);
        if (!isNaN(parsedQuantity) && parsedQuantity >= 0) {
            handleUpdateQuantity(productId, parsedQuantity);
        } else {
            const originalQuantity = cart.cartItems.find(item => item.productId === productId).quantity;
            setInputValues(prev => ({
                ...prev,
                [productId]: originalQuantity.toString(),
            }));
        }
    };

    const handleRemoveItem = (productId) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "This will remove the item from your cart!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, remove it!',
            cancelButtonText: 'No, keep it'
        }).then((result) => {
            if (result.isConfirmed) {
                fetch(`${process.env.REACT_APP_API_URL}/cart/${productId}/remove-from-cart`, {
                    method: 'PATCH',
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`
                    }
                })
                .then(response => response.json())
                .then(data => {
                    if (data.success) {
                        setCart(data.updatedCart);
                        Swal.fire({
                            icon: 'success',
                            title: 'Item removed from cart successfully',
                        });
                    } else {
                        throw new Error(data.message || 'Failed to remove item');
                    }
                })
                .catch(error => {
                    console.error("Error removing item:", error);
                    Swal.fire({
                        icon: 'error',
                        title: 'Error removing item',
                        text: error.message
                    });
                });
            }
        });
    };

    const handleUpdateQuantity = (productId, newQuantity) => {
        if (newQuantity <= 0) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Quantity',
                text: 'Quantity must be greater than zero',
            });
            return;
        }

        fetch(`${process.env.REACT_APP_API_URL}/cart/${productId}/update-cart-quantity`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({ quantity: newQuantity })
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                setCart(data.updatedCart);
                console.log('Quantity updated successfully');
            } else {
                throw new Error(data.message || 'Failed to update quantity');
            }
        })
        .catch(error => {
            console.error("Error updating quantity:", error);
            Swal.fire({
                icon: 'error',
                title: 'Error updating quantity',
                text: error.message
            });
        });
    };

    const handleClearCart = () => {
        Swal.fire({
            title: 'Are you sure?',
            text: "This will remove all items from your cart!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, clear it!',
            cancelButtonText: 'No, keep it'
        }).then((result) => {
            if (result.isConfirmed) {
                fetch(`${process.env.REACT_APP_API_URL}/cart/clear-cart`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${localStorage.getItem('token')}`
                    }
                })
                .then(response => response.json())
                .then(data => {
                    if (data.success) {
                        setCart({ cartItems: [], totalPrice: 0 });
                        Swal.fire({
                            icon: 'success',
                            title: 'Cart cleared successfully',
                        });
                    } else {
                        throw new Error(data.message || 'Failed to clear cart');
                    }
                })
                .catch(error => {
                    console.error("Error clearing cart:", error);
                    Swal.fire({
                        icon: 'error',
                        title: 'Error clearing cart',
                        text: error.message
                    });
                });
            }
        });
    };

    const handleSelectItem = (productId) => {
        setSelectedItems(prevSelected => {
            if (prevSelected.includes(productId)) {
                return prevSelected.filter(id => id !== productId);
            }
            return [...prevSelected, productId];
        });
    };

    const handleCheckout = () => {
        const selectedCartItems = cart.cartItems.filter(item => selectedItems.includes(item.productId));
        const totalCheckoutPrice = selectedCartItems.reduce((total, item) => total + (item.quantity * item.subtotal), 0);

        fetch(`${process.env.REACT_APP_API_URL}/orders/checkout`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({
                productsOrdered: selectedCartItems,
                totalPrice: totalCheckoutPrice
            })
        })
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                Swal.fire({
                    icon: 'success',
                    title: 'Checkout successful!',
                    text: `Your total is â‚±${totalCheckoutPrice.toLocaleString(undefined, { minimumFractionDigits: 2 })}`
                });
                setCart({ cartItems: [], totalPrice: 0 });
            } else {
                throw new Error(data.message || 'Failed to checkout');
            }
        })
        .catch(error => {
            console.error("Error during checkout:", error);
            Swal.fire({
                icon: 'error',
                title: 'Checkout error',
                text: error.message
            });
        });
    };

    if (loading) return <div>Loading...</div>;

    return (
        <Container>
            <Row className="mt-3">
                <div className="col-md-12 d-flex justify-content-between">
                    <h2>Your Cart</h2>
                    {cart && cart.cartItems && cart.cartItems.length > 0 && (
                        <>
                            <Button variant="danger" onClick={handleClearCart} style={{ marginBottom: '20px' }}>
                                Clear Cart
                            </Button>
                            <Button variant="success" onClick={handleCheckout} style={{ marginBottom: '20px' }}>
                                Checkout
                            </Button>
                        </>
                    )}
                </div>
            </Row>
            {cart && cart.cartItems && cart.cartItems.length > 0 ? (
                <Row>
                    {cart.cartItems.map(item => (
                        <Col md={3} key={item.productId}>
                            <input 
                                type="checkbox" 
                                onChange={() => handleSelectItem(item.productId)} 
                                checked={selectedItems.includes(item.productId)} 
                            />
                            <CartItem
                                item={item}
                                inputValues={inputValues}
                                handleInputChange={handleInputChange}
                                handleQuantityBlur={handleQuantityBlur}
                                handleRemoveItem={handleRemoveItem}
                            />
                        </Col>
                    ))}
                </Row>
            ) : (
                <Row>
                    <Col md={12}>
                        <h4>Your cart is empty</h4>
                    </Col>
                </Row>
            )}
        </Container>
    );
};

export default CartView;
