import { useState, useEffect } from 'react';

export default function Slideshow() {
    const [photo, setPhoto] = useState(1);

    const returnPhotoURL = () => {
        switch (photo) {
            case 1:
                return "https://wallpapers.com/images/hd/uniqlo-u-by-lemaire-i0qjy90oxdsflgqe.jpg";
            case 2:
                return "https://assets.teenvogue.com/photos/5e4eb6832d1e86000856ef5a/16:9/w_2560%2Cc_limit/17_4810_v4-LR.jpg";
            case 3:
                return "https://media.femalemag.com.sg/public/2021/01/Uniqlo-U-SS21-Campaign-Assets-5-1.jpg";
            case 4:
                return "https://cdn.tatlerasia.com/asiatatler/i/ph/2018/11/05131447-uniqlo-airism-6_cover_1920x1280.jpg";
            default:
                return null;
        }
    };

    // Change the image every 3 seconds
    useEffect(() => {
        const interval = setInterval(() => {
            setPhoto((prevPhoto) => (prevPhoto >= 4 ? 1 : prevPhoto + 1));
        }, 3000); // 3000ms = 3 seconds

        return () => clearInterval(interval); // Clean up interval on unmount
    }, []);

    return (
        <div className="container-fluid p-0">
            <div className="mx-auto" style={{ maxWidth: '1080px', height: '500px', overflow: 'hidden' }}>
                <img
                    src={returnPhotoURL()}
                    alt="Hero"
                    className="w-100 h-100 mw-100"
                    style={{ objectFit: 'cover', transition: 'opacity 0.5s ease-in-out' }}
                />
            </div>
        </div>
    );
}
