import { useContext } from 'react';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { NavLink } from 'react-router-dom';
import UserContext from '../context/UserContext';

export default function AppNavbar() {
    const { user } = useContext(UserContext);

    return (
        <Navbar className="shadow-sm" expand="lg">
            <div className="nav-top">
                <Container>
                    <div className="row">
                        <div className="col-md-12">ORDER ONLINE OR CALL US (000) 000-0000</div>
                    </div>
                </Container>
            </div>
            <Container>
                <Navbar.Brand as={NavLink} to="/">CLODES</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav>
                        <Nav.Link as={NavLink} to="/">Home</Nav.Link>
                        {user.id !== null 
                            ? <>
                                <Nav.Link as={NavLink} to="/products">Products</Nav.Link>
                                <Nav.Link as={NavLink} to="/orders">
                                    {user.isAdmin ? 'Orders' : 'My Orders'}
                                </Nav.Link>
                                {!user.isAdmin && <Nav.Link as={NavLink} to="/cart">Cart</Nav.Link>}
                                <Nav.Link as={NavLink} to="/logout">Logout</Nav.Link>
                              </>
                            : <>
                                <Nav.Link as={NavLink} to="/login">Login</Nav.Link>
                                <Nav.Link as={NavLink} to="/register">Register</Nav.Link>
                              </>
                        }
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
}
