import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, Button } from 'react-bootstrap';

const CartItem = ({ item, inputValue, onInputChange, onQuantityBlur, onRemoveItem, onUpdateQuantity }) => {
    const [product, setProduct] = useState(null);
    const [localQuantity, setLocalQuantity] = useState(inputValue);

    useEffect(() => {
        // Fetch product details using the productId
        const fetchProduct = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/products/${item.productId}`);
                setProduct(response.data);
            } catch (error) {
                console.error('Error fetching product details:', error);
            }
        };

        fetchProduct();
    }, [item.productId]);

    const handleInputChange = (value) => {
        setLocalQuantity(value);
        onInputChange(item.productId, value);
    };

    const handleBlur = () => {
        onQuantityBlur(item.productId);
        onUpdateQuantity(item.productId, localQuantity);
    };

    if (!product) {
        return <div>Loading...</div>; // or some loading spinner
    }

    return (
        <Card key={item.productId}>
            <Card.Body>
                <Card.Title>{product.name}</Card.Title>
                <Card.Text>
                    <strong>Quantity:</strong>
                    <input 
                        type="number" 
                        value={localQuantity || ''} 
                        min="0"
                        onChange={(e) => handleInputChange(e.target.value)}
                        onBlur={handleBlur}
                    /> <br />
                    <strong>Subtotal:</strong> â‚±{item.subtotal.toLocaleString(undefined, { minimumFractionDigits: 2 })}
                </Card.Text>
                <Button variant="danger" onClick={() => onRemoveItem(item.productId)}>
                    Remove
                </Button>
            </Card.Body>
        </Card>
    );
};

export default CartItem;
