import React, { useEffect, useState, useContext } from 'react';
import { Container } from 'react-bootstrap';
import Swal from 'sweetalert2';
import UserContext from '../context/UserContext';
import AdminOrderView from '../components/AdminOrderView';
import UserOrderView from '../components/UserOrderView';

const OrderView = () => {
    const { user } = useContext(UserContext);
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const isAdmin = user.isAdmin; // Access user role from context

    useEffect(() => {
        const fetchOrders = async () => {
            const url = user.isAdmin ? `${process.env.REACT_APP_API_URL}/orders/all-orders` : `${process.env.REACT_APP_API_URL}/orders/my-orders`;
            const token = localStorage.getItem('token');

            try {
                const response = await fetch(url, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (!response.ok) {
                    const errorBody = await response.text();
                    throw new Error(`Failed to fetch orders. Status: ${response.status}, Body: ${errorBody}`);
                }

                const data = await response.json();
                setOrders(data.orders || []);
            } catch (error) {
                console.error('Error fetching orders:', error);
                Swal.fire({
                    icon: 'error',
                    title: 'Error fetching orders',
                    text: error.message,
                });
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, [isAdmin]);

    if (loading) return <div>Loading...</div>;

    return (
        <Container>
            <h2>{isAdmin ? 'All Orders' : 'My Orders'}</h2>
            {isAdmin ? (
                <AdminOrderView orders={orders} />
            ) : (
                <UserOrderView orders={orders} />
            )}
        </Container>
    );
};

export default OrderView;
