import { useState, useContext } from 'react';
import { Form, Button } from 'react-bootstrap';
import { Navigate, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import UserContext from '../context/UserContext';

export default function AddProduct() {
	const navigate = useNavigate();
	const { user } = useContext(UserContext);

	// Input states
	const [name, setName] = useState("");
	const [description, setDescription] = useState("");
	const [price, setPrice] = useState("");
	const [image, setImage] = useState(null); // New state for image
	const [loading, setLoading] = useState(false); // New loading state

	function createProduct(e) {
		e.preventDefault();
		setLoading(true); // Start loading state

		let token = localStorage.getItem('token');
		let formData = new FormData();

		// Append form data
		formData.append('name', name);
		formData.append('description', description);
		formData.append('price', price);
		formData.append('image', image); // Append the image file

		fetch(`${process.env.REACT_APP_API_BASE_URL}/products/`, {
			method: 'POST',
			headers: {
				'Authorization': `Bearer ${token}` // Authorization header
			},
			body: formData // Send as formData instead of JSON
		})
		.then(res => {
			if (!res.ok) {
				// Handle non-2xx responses
				return res.json().then(err => {
					throw new Error(err.message || 'Failed to create product');
				});
			}
			return res.json();
		})
		.then(data => {
			if (data.message === "Product added successfully") {
				// Reset form states
				setName("");
				setDescription("");
				setPrice(0);
				setImage(null);

				Swal.fire({
					icon: 'success',
					title: 'Success',
					text: 'Product Creation Successful'
				}).then(() => {
					// Navigate to the products page
					navigate("/products");
				});
			} else {
				Swal.fire({
					icon: 'error',
					title: 'Error',
					text: data.message || 'Product already exists.'
				});
			}
		})
		.catch(error => {
			Swal.fire({
				icon: 'error',
				title: 'Error',
				text: error.message || 'Something Went Wrong.'
			});
		})
		.finally(() => {
			setLoading(false); // End loading state
		});
	}

	return (
		(user.isAdmin === true) ? (
			<>
				<h1 className="my-5 text-center">Add Product</h1>
				<Form onSubmit={e => createProduct(e)} encType="multipart/form-data">
					<Form.Group>
						<Form.Label>Name:</Form.Label>
						<Form.Control
							type="text"
							placeholder="Enter Name"
							required
							value={name}
							onChange={e => setName(e.target.value)}
						/>
					</Form.Group>
					<Form.Group>
						<Form.Label>Description:</Form.Label>
						<Form.Control
							type="text"
							placeholder="Enter Description"
							required
							value={description}
							onChange={e => setDescription(e.target.value)}
						/>
					</Form.Group>
					<Form.Group>
						<Form.Label>Price:</Form.Label>
						<Form.Control
							type="number"
							placeholder="Enter Price"
							required
							value={price}
							onChange={e => setPrice(e.target.value)}
						/>
					</Form.Group>
					<Form.Group>
						<Form.Label>Image:</Form.Label>
						<Form.Control
							type="file"
							onChange={e => setImage(e.target.files[0])} // Handle file upload
						/>
					</Form.Group>
					<Button variant="primary" type="submit" className="my-5" disabled={loading}>
						{loading ? 'Submitting...' : 'Submit'}
					</Button>
				</Form>
			</>
		) : (
			<Navigate to="/products" />
		)
	);
}
