import { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import Swal from 'sweetalert2';

export default function UpdateProductPage() {
    const { productId } = useParams(); // Get productId from the URL
    const navigate = useNavigate();

    const [product, setProduct] = useState({
        name: '',
        description: '',
        price: '',
        isActive: true,
        imageUrl: ''
    });
    
    const [loading, setLoading] = useState(true);
    const [tempImage, setTempImage] = useState(null); // For storing temporary image data
    const [imageChanged, setImageChanged] = useState(false); // To track if image is changed

    // Fetch product data from the server when the component mounts
    useEffect(() => {
        // Fetch the product information from the backend using the productId
        fetch(`${process.env.REACT_APP_API_BASE_URL}/products/${productId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('token')}` // Add your token if needed
            }
        })
        .then((res) => res.json())
        .then((data) => {
            if (data.error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error fetching product data',
                    text: data.message
                });
                setLoading(false);
            } else {
                setProduct({
                    name: data.product.name,
                    description: data.product.description,
                    price: data.product.price,
                    isActive: data.product.isActive,
                    imageUrl: data.product.imageUrl // Set the fetched image URL
                });
                setLoading(false);
            }
        })
        .catch((error) => {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: error.message
            });
            setLoading(false);
        });
    }, [productId]);

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setProduct({
            ...product,
            [name]: value
        });
    };

    // Handle image upload
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setTempImage(URL.createObjectURL(file)); // Preview the new image
            setImageChanged(true); // Mark that the image has been changed
        }
    };

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('image', tempImage ? tempImage : product.imageUrl); // Append the new image or the existing image
        formData.append('name', product.name);
        formData.append('description', product.description);
        formData.append('price', product.price);
        formData.append('isActive', product.isActive);

        fetch(`${process.env.REACT_APP_API_BASE_URL}/products/${productId}/update`, {
            method: 'PATCH',
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}` // Add your token if needed
            },
            body: formData
        })
        .then((res) => res.json())
        .then((data) => {
            if (data.message === "Product updated successfully") {
                Swal.fire({
                    icon: 'success',
                    title: 'Product Updated Successfully'
                });
                navigate('/products');
            } else {
                Swal.fire({
                    icon: 'error',
                    title: 'Update Failed',
                    text: data.message
                });
            }
        })
        .catch((error) => {
            Swal.fire({
                icon: 'error',
                title: 'An error occurred',
                text: error.message
            });
        });
    };

    // Handle cancel action
    const handleCancel = () => {
        if (imageChanged) {
            // Here, you would delete the temporary image
            // Implement the logic to delete the temporary image on the server
            // e.g., API call to delete the temporary image
        }
        navigate('/products'); // Navigate back to the products page
    };

    if (loading) {
        return <div>Loading...</div>; // Show loading indicator while fetching data
    }

    return (
        <div className="update-product-page container mt-5">
            <div className="d-flex">
                <Link className="btn btn-primary" to={"/products"}>Go Back</Link>
                <h2>Update Product</h2>
            </div>
            <form onSubmit={handleSubmit} className="update-product-form">
                <div className="form-group">
                    <label htmlFor="name">Product Name</label>
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        name="name"
                        value={product.name}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="description">Description</label>
                    <textarea
                        className="form-control"
                        id="description"
                        name="description"
                        value={product.description}
                        onChange={handleChange}
                        required
                    ></textarea>
                </div>

                <div className="form-group">
                    <label htmlFor="price">Price</label>
                    <input
                        type="number"
                        className="form-control"
                        id="price"
                        name="price"
                        value={product.price}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="isActive">Active Status</label>
                    <select
                        className="form-control"
                        id="isActive"
                        name="isActive"
                        value={product.isActive}
                        onChange={handleChange}
                    >
                        <option value={true}>Active</option>
                        <option value={false}>Inactive</option>
                    </select>
                </div>

                <div className="form-group">
                    <label htmlFor="image">Product Image</label>
                    <input
                        type="file"
                        className="form-control"
                        id="image"
                        name="image"
                        onChange={handleImageChange}
                    />
                    {tempImage && (
                        <img src={tempImage} alt="Temporary" style={{ width: '100px', height: '100px', marginTop: '10px' }} />
                    )}
                    {product.imageUrl && !tempImage && (
                        <img src={`${process.env.REACT_APP_API_BASE_URL}/public/${product.imageUrl}`} alt="Current" style={{ width: '100px', height: '100px', marginTop: '10px' }} />
                    )}
                </div>

                <button type="button" className="btn btn-secondary" onClick={handleCancel}>Cancel</button>
                <button type="submit" className="btn btn-primary">Update Product</button>
            </form>
        </div>
    );
}
