import { useContext, useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import UserContext from '../context/UserContext';

export default function Logout() {
    const { unsetUser } = useContext(UserContext);
    const [isLoggedOut, setIsLoggedOut] = useState(false); // State to manage logout status

    useEffect(() => {
        const showLogoutConfirmation = async () => {
            const result = await Swal.fire({
                title: 'Are you sure?',
                text: "You will be logged out!",
                icon: 'warning',
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: 'Yes, logout!',
                cancelButtonText: 'Cancel'
            });

            if (result.isConfirmed) {
                unsetUser();
                setIsLoggedOut(true); // Update state to indicate user has logged out
                Swal.fire(
                    'Logged out!',
                    'You have been logged out.',
                    'success'
                );
            }
        };

        showLogoutConfirmation();
    }, [unsetUser]);

    // Redirect to login page if logged out
    if (isLoggedOut) {
        return <Navigate to='/login' />;
    }

    return null; // Render nothing while waiting for confirmation
}
