import React from 'react';
import { Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Slideshow from '../components/Slideshow';
import PreviewProducts from '../components/PreviewProducts';
import Container from 'react-bootstrap/Container';

export default function Home() {
    return (
        <>
            <Container>
            {/* Slideshow Section */}
            <Row className="min-vh-100 d-flex align-items-center">
                <Col className="my-5">
                    <Slideshow />
                </Col>
            </Row>
            </Container>

            {/* CAPSTONE Section with Placeholder Background */}
            <Row
                className="min-vh-100 d-flex align-items-center"
                style={{
                    backgroundImage: 'url(https://via.placeholder.com/1920x1080)', // Placeholder image
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                }}
            >
                <Col>
                    <h1 style={{ color: '#fff' }}>CAPSTONE</h1>
                    <p style={{ color: '#fff' }}>Designed for everyday wear.</p>
                    <Link className="p-3 btn btn-success" to="/products">
                        Browse Products
                    </Link>
                </Col>
            </Row>

            {/* Preview Products Section */}
            <Row className="min-vh-100 d-flex align-items-center">
                <Col>
                    <PreviewProducts />
                </Col>
            </Row>
        </>
    );
}
