import { useContext, useEffect, useState } from 'react';
import { useNavigate, Navigate, useLocation } from 'react-router-dom'; // Import useLocation
import Swal from 'sweetalert2';
import UserContext from '../context/UserContext';

export default function Logout() {
    const { unsetUser } = useContext(UserContext);
    const [isLoggedOut, setIsLoggedOut] = useState(false); // State to manage logout status
    const navigate = useNavigate(); // Hook to programmatically navigate
    const location = useLocation(); // Hook to get the current location

    useEffect(() => {
        const showLogoutConfirmation = async () => {
            // Check if the current location is not the login page
            if (location.pathname !== '/login') {
                const result = await Swal.fire({
                    title: 'Are you sure?',
                    text: "You will be logged out!",
                    icon: 'warning',
                    showCancelButton: true,
                    confirmButtonColor: '#3085d6',
                    cancelButtonColor: '#d33',
                    confirmButtonText: 'Yes, logout!',
                    cancelButtonText: 'Cancel'
                });

                if (result.isConfirmed) {
                    unsetUser();
                    setIsLoggedOut(true); // Update state to indicate user has logged out
                    Swal.fire(
                        'Logged out!',
                        'You have been logged out.',
                        'success'
                    );
                } else {
                    navigate(-1); // Navigate back to the previous page
                }
            } else {
                // If already on the login page, just log out without showing the prompt
                unsetUser();
                setIsLoggedOut(true);
            }
        };

        showLogoutConfirmation();
    }, [unsetUser, navigate, location]); // Add `location` to dependencies

    // Redirect to login page if logged out
    if (isLoggedOut) {
        return <Navigate to='/login' />;
    }

    return null; // Render nothing while waiting for confirmation
}
