import { useState, useContext } from 'react';
import { Form, Button, Container } from 'react-bootstrap';
import { Navigate, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import UserContext from '../context/UserContext';

export default function AddProduct() {
	const navigate = useNavigate();
	const { user } = useContext(UserContext);

	// Input states
	const [name, setName] = useState("");
	const [description, setDescription] = useState("");
	const [price, setPrice] = useState("");
	const [image, setImage] = useState(null); // New state for image
	const [loading, setLoading] = useState(false); // New loading state

	const createProduct = async (e) => {
		e.preventDefault();
		setLoading(true); // Start loading state

		let token = localStorage.getItem('token');
		let formData = new FormData();

		// Append form data
		formData.append('name', name);
		formData.append('description', description);
		formData.append('price', price);
		formData.append('image', image); // Append the image file

		try {
		    const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/products/`, {
		        method: 'POST',
		        headers: {
		            'Authorization': `Bearer ${token}`, // Authorization header
		        },
		        body: formData, // Send as FormData instead of JSON
		    });

		    // Check if the response is okay
		    if (!response.ok) {
		        const errorData = await response.text(); // Get response as text for HTML responses
		        console.error('Server Error:', errorData); // Log the error for debugging
		        throw new Error('Server error, please check the console for more details.');
		    }

		    const data = await response.json();

		    if (data.message === "Product added successfully") {
		        // Reset form states
		        setName("");
		        setDescription("");
		        setPrice(0);
		        setImage(null);

		        Swal.fire({
		            icon: 'success',
		            title: 'Success',
		            text: 'Product Creation Successful',
		        }).then(() => {
		            // Navigate to the products page
		            navigate("/products");
		        });
		    } else {
		        Swal.fire({
		            icon: 'error',
		            title: 'Error',
		            text: data.message || 'Product already exists.',
		        });
		    }
		} catch (error) {
		    Swal.fire({
		        icon: 'error',
		        title: 'Error',
		        text: error.message || 'Something Went Wrong.',
		    });
		} finally {
		    setLoading(false); // End loading state
		}

	};

	return (
		(user.isAdmin === true) ? (
			<Container fluid>
				<Container>
					<h1 className="my-5 text-center">Add Product</h1>
					<Form onSubmit={createProduct} encType="multipart/form-data">
						<Form.Group>
							<Form.Label>Name:</Form.Label>
							<Form.Control
								type="text"
								placeholder="Enter Name"
								required
								value={name}
								onChange={e => setName(e.target.value)}
							/>
						</Form.Group>
						<Form.Group>
							<Form.Label>Description:</Form.Label>
							<Form.Control
								type="text"
								placeholder="Enter Description"
								required
								value={description}
								onChange={e => setDescription(e.target.value)}
							/>
						</Form.Group>
						<Form.Group>
							<Form.Label>Price:</Form.Label>
							<Form.Control
								type="number"
								placeholder="Enter Price"
								required
								value={price}
								onChange={e => setPrice(e.target.value)}
							/>
						</Form.Group>
						<Form.Group>
							<Form.Label>Image:</Form.Label>
							<Form.Control
								type="file"
								onChange={e => setImage(e.target.files[0])} // Handle file upload
							/>
						</Form.Group>
						<Button variant="primary" type="submit" className="my-5" disabled={loading}>
							{loading ? 'Submitting...' : 'Submit'}
						</Button>
					</Form>
				</Container>
			</Container>
		) : (
			<Navigate to="/products" />
		)
	);
}
